import React, { useState } from "react";
// import Grid from "@material-ui/core/Grid";
// import TextField from "@material-ui/core/TextField";
// import FormControlLabel from "@material-ui/core/FormControlLabel";
// import FormControl from "@material-ui/core/FormControl";
// import FormLabel from "@material-ui/core/FormLabel";
// import RadioGroup from "@material-ui/core/RadioGroup";
// import Radio from "@material-ui/core/Radio";
// import Select from "@material-ui/core/Select";
// import MenuItem from "@material-ui/core/MenuItem";
// import Slider from "@material-ui/core/Slider";
// import Button from "@material-ui/core/Button";
// import Box from '@mui/material/Box';
// import TextField from '@mui/material/TextField';
import {
  Form,
  Button,
  FormGroup,
  FormControl,
  ControlLabel,
  Row,
  Col,
} from "react-bootstrap";
import { makeStyles } from "@material-ui/core/styles";

// const useStyles = makeStyles((theme) => ({

// }));

export default function FormCard() {
  // const classes = useStyles();

  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");
  const [fullName, setFullName] = useState("");
  const [onePunchLiner, setOnePunchLiner] = useState("");
  const [story, setStory] = useState("");
  const [designSample, setDesignSample] = useState("");
  const submit = (e) => {
    e.preventDefault();
    if (!email || !contact || !fullName) {
      alert("Full name or email or contact cannot be blank");
    } else {
      // addTodo(title, desc);
      setEmail("");
      setContact("");
      setFullName("");
      setOnePunchLiner("");
      setStory("");
      setDesignSample("");
    }
  };
  return (
    <Form>
      <Form.Group className="mb-3" controlId="formBasicName">
        <Form.Label>Full Name</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter email"
          value={fullName}
          onChange={(e) => {
            setFullName(e.target.value);
          }}
          required
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => {
            setEmail(e.target.value);
          }}
          required
        />
        <Form.Control.Feedback type="invalid">
          Please enter valid email.
        </Form.Control.Feedback>
        {/* <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text> */}
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicContact">
        <Form.Label>Contact Number</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter contact number"
          value={contact}
          onChange={(e) => {
            setContact(e.target.value);
          }}
          required
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicOnePunchLiner">
        <Form.Label>One Punch Line</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter your awesome funny one punch liner"
          value={onePunchLiner}
          onChange={(e) => {
            setOnePunchLiner(e.target.value);
          }}
          required
        />
      </Form.Group>
      <Form.Group controlId="formBasicStory" className="mb-3">
        <Form.Label>Story behind your one punch liner</Form.Label>
        <Form.Control
          type="file"
          placeholder="Enter the story behind your great one punch liner"
          value={story}
          onChange={(e) => {
            setStory(e.target.value);
          }}
        />
      </Form.Group>
      <Button variant="primary" type="submit" onClick={submit}>
        Submit
      </Button>
    </Form>
  );
}
